    public Vector getCheckedBox(String values, String delim) {
        Vector vctChecked = new Vector();
        try {
            StringTokenizer st = new StringTokenizer(values, delim);
            while (st.hasMoreTokens())
                vctChecked.add(st.nextToken());
        } catch (Exception e) {
            vctChecked.add("");
        }
        return vctChecked;
    }


